import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axiosDB from '../../../../utils/axios/axiosConfig'
import styles from './YearReward.module.css'

const tg = window.Telegram.WebApp

const YearReward = ({ telegramId }) => {
	const [isYearChecked, setIsYearChecked] = useState(false)
	const [rewardAdded, setRewardAdded] = useState(null)
	const [years, setYears] = useState(null)
	const [loading, setLoading] = useState(true)
	const [dataFetched, setDataFetched] = useState(false) // New state for tracking data fetch
	const navigate = useNavigate()

	useEffect(() => {
		let fakeProgress = 0
		const fakeProgressInterval = setInterval(() => {
			if (fakeProgress >= 100) {
				clearInterval(fakeProgressInterval)
			} else {
				fakeProgress += 5
				document.getElementById(
					'fake-progress'
				).style.width = `${fakeProgress}%`
			}
		}, 100)

		axiosDB
			.post(`/bonus/${telegramId}`)
			.then(result => {
				if (result.status === 200) {
					setRewardAdded(result.data.reward)
					setYears(result.data.years)
					setDataFetched(true) // Data has been fetched
				}
			})
			.catch(error => {
				console.error('Error fetching reward:', error)
			})
			.finally(() => {
				setLoading(false)
			})
	}, [telegramId])

	useEffect(() => {
		const minimumDelay = 4000
		const startTime = Date.now()

		if (dataFetched) {
			const remainingTime = minimumDelay - (Date.now() - startTime)
			setTimeout(() => {
				setIsYearChecked(true)
			}, Math.max(remainingTime, 0))
		}
	}, [dataFetched])

	useEffect(() => {
		if (isYearChecked) {
			tg.HapticFeedback.impactOccurred('light')
		}
	}, [isYearChecked])

	const handleGetReward = () => {
		navigate('/main')
		console.log('clicked')
	}

	return (
		<div className={styles.container}>
			<p className={styles.heading}>Let's check how old you are on Telegram</p>
			<div className={styles.progressContainer}>
				<div
					id='fake-progress'
					className={styles.fakeProgressBar}
					style={{ width: '0%' }}
				/>
				<div
					className={styles.progressBar}
					style={{ width: isYearChecked ? '100%' : '0%' }}
				/>
			</div>
			{!loading && isYearChecked && (
				<div>
					<h1 className={styles.years}>{years}</h1>
					<p className={styles.rewardText}>Reward {rewardAdded} soldo!</p>
					<button
						className={styles.gradient_btn}
						onClick={() => handleGetReward()}
					>
						Thank you
					</button>
				</div>
			)}
		</div>
	)
}

export default YearReward
