import React, { useState } from 'react'
import { animated, useSpring } from 'react-spring'
import { useDrag } from 'react-use-gesture'

const images = [
	'https://avatars.mds.yandex.net/i?id=0a8dc1da8b47ac63e1b06dadc55e1a3f_l-5141109-images-thumbs&n=27&h=480&w=480',
	'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRxR9HDlnMcTgDlh-WyL8lmU5BCRl7cJaVZvw&s',
	'https://masterpiecer-images.s3.yandex.net/0e40edb447e111ee9bda5a1112d6d6c5:upscaled',
]

const Manual = () => {
	const [currentIndex, setCurrentIndex] = useState(0)

	const [{ x }, set] = useSpring(() => ({ x: 0 }))

	const bind = useDrag(
		({ down, movement: [mx], direction: [xDir], distance, cancel }) => {
			if (down && distance > window.innerWidth / 4) {
				setCurrentIndex(
					prevIndex =>
						(prevIndex + (xDir > 0 ? -1 : 1) + images.length) % images.length
				)
				cancel()
			}
			set({ x: down ? mx : 0 })
		}
	)

	const progressBar = () => {
		return (
			<div
				style={{ display: 'flex', justifyContent: 'center', marginTop: '10px' }}
			>
				{images.map((_, index) => (
					<div
						key={index}
						style={{
							width: '30px',
							height: '5px',
							backgroundColor: index === currentIndex ? 'white' : 'gray',
							margin: '0 5px',
							transition: 'background-color 0.3s',
						}}
					/>
				))}
			</div>
		)
	}

	return (
		<div
			style={{
				width: '100%',
				height: '100vh',
				position: 'relative',
				overflow: 'hidden',
			}}
		>
			<animated.div
				{...bind()}
				style={{
					width: '100%',
					height: '100%',
					backgroundImage: `url(${images[currentIndex]})`,
					backgroundSize: 'cover',
					backgroundPosition: 'center',
					x,
					touchAction: 'none',
					cursor: 'grab',
				}}
			/>
			{progressBar()}
		</div>
	)
}

export default Manual
