import { useDrag } from '@use-gesture/react'
import React, { useEffect, useState } from 'react'
import { animated, useSpring } from 'react-spring'

const images = [
	'https://avatars.mds.yandex.net/i?id=0a8dc1da8b47ac63e1b06dadc55e1a3f_l-5141109-images-thumbs&n=27&h=480&w=480',
	'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRxR9HDlnMcTgDlh-WyL8lmU5BCRl7cJaVZvw&s',
	'https://masterpiecer-images.s3.yandex.net/0e40edb447e111ee9bda5a1112d6d6c5:upscaled',
]

const Manual = () => {
	const [currentIndex, setCurrentIndex] = useState(0)
	const [isPaused, setIsPaused] = useState(false)

	const [{ x }, set] = useSpring(() => ({ x: 0 }))

	const bind = useDrag(
		({ down, movement: [mx], direction: [xDir], distance, cancel }) => {
			if (down && distance > window.innerWidth / 4) {
				const newIndex =
					(currentIndex + (xDir > 0 ? -1 : 1) + images.length) % images.length
				setCurrentIndex(newIndex)
				set({ x: 0 })
				cancel()
			} else {
				set({ x: down ? mx : 0 })
			}
		}
	)

	const progressBar = () => {
		return (
			<div
				style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}
			>
				{images.map((_, index) => (
					<div
						key={index}
						style={{
							width: '10px',
							height: '10px',
							borderRadius: '50%',
							backgroundColor: index === currentIndex ? 'white' : 'gray',
							margin: '0 5px',
							transition: 'background-color 0.3s',
						}}
					/>
				))}
			</div>
		)
	}

	const handlePrev = () => {
		setCurrentIndex((currentIndex - 1 + images.length) % images.length)
	}

	const handleNext = () => {
		setCurrentIndex((currentIndex + 1) % images.length)
	}

	// Auto-slide functionality
	useEffect(() => {
		if (!isPaused) {
			const interval = setInterval(() => {
				handleNext()
			}, 3000) // Change slide every 3 seconds

			return () => clearInterval(interval)
		}
	}, [currentIndex, isPaused])

	return (
		<div
			style={{
				width: '100%',
				height: '100vh',
				position: 'relative',
				overflow: 'hidden',
				backgroundColor: '#000',
				display: 'flex',
				justifyContent: 'center',
				alignItems: 'center',
			}}
			onMouseEnter={() => setIsPaused(true)}
			onMouseLeave={() => setIsPaused(false)}
		>
			<animated.div
				{...bind()}
				style={{
					width: '100%',
					height: '100%',
					backgroundImage: `url(${images[currentIndex]})`,
					backgroundSize: 'cover',
					backgroundPosition: 'center',
					x,
					touchAction: 'none',
					cursor: 'grab',
					position: 'absolute',
					transition: 'transform 0.5s ease-in-out',
				}}
			/>

			{/* Previous Button */}
			<button
				onClick={handlePrev}
				style={{
					position: 'absolute',
					left: '20px',
					top: '50%',
					transform: 'translateY(-50%)',
					background: 'rgba(255, 255, 255, 0.5)',
					border: 'none',
					padding: '10px',
					borderRadius: '50%',
					cursor: 'pointer',
				}}
			>
				&#10094;
			</button>

			{/* Next Button */}
			<button
				onClick={handleNext}
				style={{
					position: 'absolute',
					right: '20px',
					top: '50%',
					transform: 'translateY(-50%)',
					background: 'rgba(255, 255, 255, 0.5)',
					border: 'none',
					padding: '10px',
					borderRadius: '50%',
					cursor: 'pointer',
				}}
			>
				&#10095;
			</button>

			{/* Progress Dots */}
			{progressBar()}
		</div>
	)
}

export default Manual
